import React from 'react'
import Book from "./Book"


function BookList({books,onDeleteBook,}){

    async function handleLoadValues(type) {
        const JsonData = await getBooks();
        let typeData = [];
        if (type === 'author') {
            document.getElementById('search-box').placeholder = 'Enter Author name';
            JsonData.forEach(d => d.authors.forEach(author => {
                if (!typeData.includes(author)) {
                    typeData.push(author);
                }
            }));
        } else if (type === 'name') {
            document.getElementById('search-box').placeholder = 'Search by Book name';
            JsonData.forEach(d => {
                    if(!typeData.includes(d.title)){
                        typeData.push(d.title);
                    }
                }
            );

        } else if (type === 'category') {
            document.getElementById('search-box').placeholder = 'Search By Categories';
            JsonData.forEach(d => d.categories.forEach(category => {
                if (!typeData.includes(category)) {
                    typeData.push(category);
                }
            }));
        } else if (type === 'pageCount') {
            document.getElementById('search-box').placeholder = 'Enter page Count';
        } else {
            document.getElementById('search-box').placeholder = 'Enter Book isbn';
            JsonData.forEach(d => {
                    if(!typeData.includes(d.isbn)){
                        typeData.push(d.isbn);
                    }
                }
            );

        }

        await populateOptions(typeData);


    }

    async function populateOptions(data) {
        let list = document.getElementById('list');
        list.innerHTML = '';
        data.forEach(d => {
            let option = document.createElement('option');
            option.value = option.text = d;
            list.appendChild(option);
        });
    }


    return <>

        <form>
            <h4>Select Search Criteria</h4>
            <div id="search-options">
                <div>
                    <input type="radio" id="category"
                           name="search"
                           value="category" onClick={handleLoadValues('category')}/>

                        <label htmlFor="category">Category</label>
                </div>
                <div>
                    <input type="radio" id="name"
                           name="search"
                           value="name" onClick={handleLoadValues('name')}/>
                        <label htmlFor="name">Book Name</label>
                </div>
                <div>
                    <input type="radio" id="author"
                           name="search"
                           value="author" onClick={handleLoadValues('author')}/>
                        <label htmlFor="author">Author Name</label>
                </div>
                <div>
                    <input type="radio" id="isbn"
                           name="search"
                           value="isbn" onClick={handleLoadValues('isbn')}/>
                        <label htmlFor="isbn">ISBN</label>
                </div>
                <div>
                    <input type="radio" id="pageCount"
                           name="search"
                           value="pageCount" onClick={handleLoadValues('pageCount')}/>
                        <label htmlFor="pageCount">Page Count</label>
                </div>
            </div>

            <div id="search-param">
                <input name="searchParam"
                       placeholder="Search parameter"
                       id="searchParam"/>
                    <input type="button"
                           value="Search"
                           id="search-btn"/>
            </div>
        </form>

        <div id="contentDiv"></div>





        <ul id="book-cards" className="cards">
            {books.map((book, index) =>
                <Book
                    onDeleteBook={() => onDeleteBook(book.isbn)}
                    key={index}
                    book={book}
                />)
            }
        </ul>
    </>
}

export default BookList
